    private fun setMenuItems() {
        userNameTool.text = "Hi " + loginData.userDetail?.userName.toString()

        val newList = ArrayList<String>()
        newList.apply {
            loginData.userDetail?.batchList?.forEach {
                this.add(it.batchName.toString())
            }
            Log.e("popData", newList.toString())
            val adapter = ArrayAdapter(requireContext(), R.layout.spinner_item, newList)
            batchSpinner.adapter = adapter

            batchSpinner.onItemSelectedListener = object : AdapterView.OnItemSelectedListener {
                override fun onItemSelected(
                    adapterView: AdapterView<*>,
                    view: View,
                    i: Int,
                    l: Long
                ) {
                    batchId = loginData.userDetail?.batchList?.get(i)?.id

                    if (!loginData.role.equals("COACH")) getAssignments(
                        loginData.userDetail?.batchList?.get(
                            i
                        )?.id!!
                    )
                }

                override fun onNothingSelected(adapterView: AdapterView<*>?) {}
            }
        }

        qrScannerTool.setOnClickListener {
            if (ContextCompat.checkSelfPermission(requireActivity(), Manifest.permission.CAMERA)
                == PackageManager.PERMISSION_GRANTED
            ) {
                openQRCodeScreen()
            } else {
                ActivityCompat.requestPermissions(
                    requireActivity(),
                    arrayOf(Manifest.permission.CAMERA),
                    100
                )
            }
        }
    }

    suspend fun sendAssignmentsToApi(
        title: String, desc: String, subject: String, batchId: String, date: String,
        teacherName: String,
    ) {

        val file: File = FileUtils.getFile(requireContext(), filePath)


        val description: RequestBody = createPartFromString(desc)!!
        val title: RequestBody = createPartFromString(title)!!
        val subject: RequestBody = createPartFromString(subject)!!
        val batchId: RequestBody = createPartFromString(batchId)!!
        val date: RequestBody = createPartFromString(date)!!
        val teacherName: RequestBody = createPartFromString(teacherName)!!

        val map: HashMap<String, RequestBody> = HashMap()
        map["title"] = title
        map["description"] = description
        map["subject"] = subject
        map["batchId"] = batchId
        map["batchId"] = batchId
        map["date"] = date
        map["teacherName"] = teacherName
        val body: MultipartBody.Part = prepareFilePart("Files", file)

        RetroFitCall.retroFitCallPostAssign()
        val service = RetroFitCall.retrofit.create(ApiInterface::class.java)
        val response = service.postAssignment(
            map,body
        )

        response.enqueue(object : retrofit2.Callback<AssignmentResponse> {
            override fun onResponse(call: retrofit2.Call<AssignmentResponse>, response: retrofit2.Response<AssignmentResponse>) {
                if (response.isSuccessful){
                    if (response.code() == 200) {
                    viewLifecycleOwner.lifecycleScope.launch {
                        if (myProgressBar.isShowing()) {
                            myProgressBar.dismiss()
                        }
                        titleTxt.text?.clear()
                        descriptionTxt.text?.clear()
                        Toast.makeText(requireContext(), "Assignment Submitted Successfully",Toast.LENGTH_SHORT).show()
                    }

                } else {

                    viewLifecycleOwner.lifecycleScope.launch {
                        if (myProgressBar.isShowing()) {
                            myProgressBar.dismiss()
                        }
                        showErrorMsg("You have no Completed Live Sessions right now")
                    }
                }
                }
            }
            override fun onFailure(call: retrofit2.Call<AssignmentResponse>, t: Throwable) {
                Toast.makeText(requireContext(), "${t.message}", Toast.LENGTH_SHORT).show()
            }
        })

    }
    private fun createPartFromString(string: String): RequestBody? {
        return RequestBody.create(MultipartBody.FORM, string)
    }
    private fun prepareFilePart(partName: String, file: File): MultipartBody.Part {
        val requestBody = RequestBody.create("image/*".toMediaTypeOrNull(), file)
        return MultipartBody.Part.createFormData(partName, file.name, requestBody)
    }

    private fun showErrorMsg(s: String) {

    }

    private fun openQRCodeScreen() {
        val intent = Intent(requireContext(), QRCodeActivity::class.java)
        startActivity(intent)
    }

    override fun onAssignmentClicked(url: String) {
        val intent = Intent(requireContext(), AttachmentActivity::class.java)
        intent.putExtra("url", url)
        requireContext().startActivity(intent)
    }

    //method to show file chooser
    private fun showFileChooser() {
        val intent = Intent()
        intent.type = "*/*"
        intent.action = Intent.ACTION_GET_CONTENT
        startActivityForResult(Intent.createChooser(intent, "Select Pdf"), PICK_PDF_REQUEST)
    }

    //handling the image chooser activity result
     override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
        super.onActivityResult(requestCode, resultCode, data)
        if (requestCode == PICK_PDF_REQUEST && resultCode == RESULT_OK && data != null && data.data != null) {
            filePath = data.data

            var inputStream = requireActivity().contentResolver.openInputStream(data.data!!)
            val pdfInBytes = inputStream?.available()?.let { byteArrayOf(it.toByte()) }
            inputStream?.read(pdfInBytes)
             val encodedPdf = Base64.encode(pdfInBytes, Base64.DEFAULT)

            Log.e("filPop", encodedPdf.toString())



        }
    }


    private fun requestStoragePermission() {
        if (ContextCompat.checkSelfPermission(
                requireContext(),
                Manifest.permission.READ_EXTERNAL_STORAGE
            ) == PackageManager.PERMISSION_GRANTED
        ) return
        if (ActivityCompat.shouldShowRequestPermissionRationale(
                requireActivity(),
                Manifest.permission.READ_EXTERNAL_STORAGE
            )
        ) {
            //If the user has denied the permission previously your code will come to this block
            //Here you can explain why you need this permission
            //Explain here why you need this permission
        }
        //And finally ask for the permission
        ActivityCompat.requestPermissions(
            requireActivity(),
            arrayOf(Manifest.permission.READ_EXTERNAL_STORAGE),
            STORAGE_PERMISSION_CODE
        )
    }


    //This method will be called when the user will tap on allow or deny
    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {

        //Checking the request code of our request
        if (requestCode == STORAGE_PERMISSION_CODE) {

            //If permission is granted
            if (grantResults.size > 0 && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                //Displaying a toast
                Toast.makeText(
                    requireContext(),
                    "Permission granted now you can read the storage",
                    Toast.LENGTH_LONG
                ).show()
            } else {
                //Displaying another toast if permission is not granted
                Toast.makeText(requireContext(), "Oops you just denied the permission", Toast.LENGTH_LONG)
                    .show()
            }
        }
    }

}

